mapper.update:
UPDATE EM_SEQ
MIN_VALUE = #{minValue}, MAX_VALUE = #{maxValue}, CURRENT_INDEX = #{currentIndex}, INCREMENT_VALUE = #{incrementValue}, ITEM_LENGTH = #{itemLength}, PREFIX = #{prefix}, POSTFIX = #{postfix}, REPEATABLE = #{repeatable}, FORMATTER = #{formatter}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ITEM_NAME = #{itemName} AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM EM_SEQ
WHERE
  ITEM_NAME = #{itemName} 
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM EM_SEQ
WHERE 1=1
  AND ITEM_NAME = #{itemName}
  AND MIN_VALUE = #{minValue}
  AND MAX_VALUE = #{maxValue}
  AND CURRENT_INDEX = #{currentIndex}
  AND INCREMENT_VALUE = #{incrementValue}
  AND ITEM_LENGTH = #{itemLength}
  AND PREFIX = #{prefix}
  AND POSTFIX = #{postfix}
  AND REPEATABLE = #{repeatable}
  AND FORMATTER = #{formatter}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ITEM_NAME AS "itemName",
  MIN_VALUE AS "minValue",
  MAX_VALUE AS "maxValue",
  CURRENT_INDEX AS "currentIndex",
  INCREMENT_VALUE AS "incrementValue",
  ITEM_LENGTH AS "itemLength",
  PREFIX AS "prefix",
  POSTFIX AS "postfix",
  REPEATABLE AS "repeatable",
  FORMATTER AS "formatter",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_SEQ
WHERE 1 = 1 
  AND ITEM_NAME = #{itemName}
  AND MIN_VALUE = #{minValue}
  AND MAX_VALUE = #{maxValue}
  AND CURRENT_INDEX = #{currentIndex}
  AND INCREMENT_VALUE = #{incrementValue}
  AND ITEM_LENGTH = #{itemLength}
  AND PREFIX = #{prefix}
  AND POSTFIX = #{postfix}
  AND REPEATABLE = #{repeatable}
  AND FORMATTER = #{formatter}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_SEQ
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ITEM_NAME = #{itemName}
  AND MIN_VALUE = #{minValue}
  AND MAX_VALUE = #{maxValue}
  AND CURRENT_INDEX = #{currentIndex}
  AND INCREMENT_VALUE = #{incrementValue}
  AND ITEM_LENGTH = #{itemLength}
  AND PREFIX = #{prefix}
  AND POSTFIX = #{postfix}
  AND REPEATABLE = #{repeatable}
  AND FORMATTER = #{formatter}
