mapper.update:
UPDATE UME_USER_CARD
CARD_TYPE = #{cardType}, CARD_NUMBER = #{cardNumber}, CARD_OWNER = #{cardOwner}, CARD_BANK = #{cardBank}, CARD_EXPIRATION_DATE = #{cardExpirationDate}, CARD_STATUS = #{cardStatus}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE USER_ID = #{userId} AND SEQ = #{seq} AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM UME_USER_CARD
WHERE
  USER_ID = #{userId} AND 
  SEQ = #{seq} 
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM UME_USER_CARD
WHERE 1=1
  AND USER_ID = #{userId}
  AND SEQ = #{seq}
  AND CARD_TYPE = #{cardType}
  AND CARD_NUMBER = #{cardNumber}
  AND CARD_OWNER = #{cardOwner}
  AND CARD_BANK = #{cardBank}
  AND CARD_EXPIRATION_DATE = #{cardExpirationDate}
  AND CARD_STATUS = #{cardStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  USER_ID AS "userId",
  SEQ AS "seq",
  CARD_TYPE AS "cardType",
  CARD_NUMBER AS "cardNumber",
  CARD_OWNER AS "cardOwner",
  CARD_BANK AS "cardBank",
  CARD_EXPIRATION_DATE AS "cardExpirationDate",
  CARD_STATUS AS "cardStatus",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM UME_USER_CARD
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND SEQ = #{seq}
  AND CARD_TYPE = #{cardType}
  AND CARD_NUMBER = #{cardNumber}
  AND CARD_OWNER = #{cardOwner}
  AND CARD_BANK = #{cardBank}
  AND CARD_EXPIRATION_DATE = #{cardExpirationDate}
  AND CARD_STATUS = #{cardStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM UME_USER_CARD
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND USER_ID = #{userId}
  AND SEQ = #{seq}
  AND CARD_TYPE = #{cardType}
  AND CARD_NUMBER = #{cardNumber}
  AND CARD_OWNER = #{cardOwner}
  AND CARD_BANK = #{cardBank}
  AND CARD_EXPIRATION_DATE = #{cardExpirationDate}
  AND CARD_STATUS = #{cardStatus}
