mapper.update:
UPDATE UME_WECHAT_USER
WECHAT_OPENID = #{wechatOpenid}, WECHAT_CITY = #{wechatCity}, WECHAT_COUNTRY = #{wechatCountry}, WECHAT_HEADIMGURL = #{wechatHeadimgurl}, WECHAT_LANGUAGE = #{wechatLanguage}, WECHAT_NICKNAME = #{wechatNickname}, WECHAT_PROVINCE = #{wechatProvince}, WECHAT_SEX = #{wechatSex}, WECHAT_UNIONID = #{wechatUnionid}, WECHAT_PRIVILEGE = #{wechatPrivilege}, WECHAT_GROUPID = #{wechatGroupid}, WECHAT_TAGID_LIST = #{wechatTagidList}, WECHAT_SUBSCRIBE = #{wechatSubscribe}, WECHAT_SUBSCRIBE_TIME = #{wechatSubscribeTime}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE USER_ID = #{userId} AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM UME_WECHAT_USER
WHERE
  USER_ID = #{userId} 
   AND UPDATE_DATETIME = #{updateDatetime}

