mapper.select:
SELECT
  USER_ID AS "userId",
  WECHAT_OPENID AS "wechatOpenid",
  WECHAT_CITY AS "wechatCity",
  WECHAT_COUNTRY AS "wechatCountry",
  WECHAT_HEADIMGURL AS "wechatHeadimgurl",
  WECHAT_LANGUAGE AS "wechatLanguage",
  WECHAT_NICKNAME AS "wechatNickname",
  WECHAT_PROVINCE AS "wechatProvince",
  WECHAT_SEX AS "wechatSex",
  WECHAT_UNIONID AS "wechatUnionid",
  WECHAT_PRIVILEGE AS "wechatPrivilege",
  WECHAT_GROUPID AS "wechatGroupid",
  WECHAT_TAGID_LIST AS "wechatTagidList",
  WECHAT_SUBSCRIBE AS "wechatSubscribe",
  WECHAT_SUBSCRIBE_TIME AS "wechatSubscribeTime",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM UME_WECHAT_USER
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND WECHAT_OPENID = #{wechatOpenid}
  AND WECHAT_CITY = #{wechatCity}
  AND WECHAT_COUNTRY = #{wechatCountry}
  AND WECHAT_HEADIMGURL = #{wechatHeadimgurl}
  AND WECHAT_LANGUAGE = #{wechatLanguage}
  AND WECHAT_NICKNAME = #{wechatNickname}
  AND WECHAT_PROVINCE = #{wechatProvince}
  AND WECHAT_SEX = #{wechatSex}
  AND WECHAT_UNIONID = #{wechatUnionid}
  AND WECHAT_PRIVILEGE = #{wechatPrivilege}
  AND WECHAT_GROUPID = #{wechatGroupid}
  AND WECHAT_TAGID_LIST = #{wechatTagidList}
  AND WECHAT_SUBSCRIBE = #{wechatSubscribe}
  AND WECHAT_SUBSCRIBE_TIME = #{wechatSubscribeTime}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM UME_WECHAT_USER
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND USER_ID = #{userId}
  AND WECHAT_OPENID = #{wechatOpenid}
