mapper.update:
UPDATE EM_TBL_CFG
ENT_NAME = #{entName}, ENT_TYPE = #{entType}, ENT_LAYOUT = #{entLayout}, REF_TBL_ID = #{refTblId}, REF_TBL_DATASOURCE = #{refTblDatasource}, REF_TBL_READONLY_COLS = #{refTblReadonlyCols}, REF_TBL_HIDE_COLS = #{refTblHideCols}, REF_TBL_DISABLE_COLS = #{refTblDisableCols}, PRE_PROC_DISP = #{preProcDisp}, PRE_PROC_SAVE = #{preProcSave}, PRE_PROC_VALIDATE = #{preProcValidate}, CUS_ENTITY_MANAGER = #{cusEntityManager}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ENT_ID = #{entId} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ENT_ID AS "entId",
  ENT_NAME AS "entName",
  ENT_TYPE AS "entType",
  ENT_LAYOUT AS "entLayout",
  REF_TBL_ID AS "refTblId",
  REF_TBL_DATASOURCE AS "refTblDatasource",
  REF_TBL_READONLY_COLS AS "refTblReadonlyCols",
  REF_TBL_HIDE_COLS AS "refTblHideCols",
  REF_TBL_DISABLE_COLS AS "refTblDisableCols",
  PRE_PROC_DISP AS "preProcDisp",
  PRE_PROC_SAVE AS "preProcSave",
  PRE_PROC_VALIDATE AS "preProcValidate",
  CUS_ENTITY_MANAGER AS "cusEntityManager",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_TBL_CFG
WHERE 1 = 1 
  AND ENT_ID = #{entId}
  AND ENT_NAME = #{entName}
  AND ENT_TYPE = #{entType}
  AND ENT_LAYOUT = #{entLayout}
  AND REF_TBL_ID = #{refTblId}
  AND REF_TBL_DATASOURCE = #{refTblDatasource}
  AND REF_TBL_READONLY_COLS = #{refTblReadonlyCols}
  AND REF_TBL_HIDE_COLS = #{refTblHideCols}
  AND REF_TBL_DISABLE_COLS = #{refTblDisableCols}
  AND PRE_PROC_DISP = #{preProcDisp}
  AND PRE_PROC_SAVE = #{preProcSave}
  AND PRE_PROC_VALIDATE = #{preProcValidate}
  AND CUS_ENTITY_MANAGER = #{cusEntityManager}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_TBL_CFG
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ENT_ID = #{entId}
  AND ENT_NAME = #{entName}
  AND ENT_TYPE = #{entType}
  AND ENT_LAYOUT = #{entLayout}
  AND REF_TBL_ID = #{refTblId}
  AND REF_TBL_DATASOURCE = #{refTblDatasource}
  AND REF_TBL_READONLY_COLS = #{refTblReadonlyCols}
  AND REF_TBL_HIDE_COLS = #{refTblHideCols}
  AND REF_TBL_DISABLE_COLS = #{refTblDisableCols}
  AND PRE_PROC_DISP = #{preProcDisp}
  AND PRE_PROC_SAVE = #{preProcSave}
  AND PRE_PROC_VALIDATE = #{preProcValidate}
  AND CUS_ENTITY_MANAGER = #{cusEntityManager}
