mapper.update:
UPDATE UME_USER_ADDRESS
ADDRESS_PROVINCE = #{addressProvince}, ADDRESS_CITY = #{addressCity}, ADDRESS_DISTRICT = #{addressDistrict}, ADDRESS_REST = #{addressRest}, CONTACT_PERSON = #{contactPerson}, CONTACT_NUMBER = #{contactNumber}, ADDRESS_TYPE = #{addressType}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE USER_ID = #{userId} AND SEQ = #{seq} AND UPDATE_DATETIME = #{updateDatetime}

mapper.delete:
DELETE FROM UME_USER_ADDRESS
WHERE
  USER_ID = #{userId} AND 
  SEQ = #{seq} 
   AND UPDATE_DATETIME = #{updateDatetime}

DELETE FROM UME_USER_ADDRESS
WHERE 1=1
  AND USER_ID = #{userId}
  AND SEQ = #{seq}
  AND ADDRESS_PROVINCE = #{addressProvince}
  AND ADDRESS_CITY = #{addressCity}
  AND ADDRESS_DISTRICT = #{addressDistrict}
  AND ADDRESS_REST = #{addressRest}
  AND CONTACT_PERSON = #{contactPerson}
  AND CONTACT_NUMBER = #{contactNumber}
  AND ADDRESS_TYPE = #{addressType}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME = #{createDatetime}
  AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  USER_ID AS "userId",
  SEQ AS "seq",
  ADDRESS_PROVINCE AS "addressProvince",
  ADDRESS_CITY AS "addressCity",
  ADDRESS_DISTRICT AS "addressDistrict",
  ADDRESS_REST AS "addressRest",
  CONTACT_PERSON AS "contactPerson",
  CONTACT_NUMBER AS "contactNumber",
  ADDRESS_TYPE AS "addressType",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM UME_USER_ADDRESS
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND SEQ = #{seq}
  AND ADDRESS_PROVINCE = #{addressProvince}
  AND ADDRESS_CITY = #{addressCity}
  AND ADDRESS_DISTRICT = #{addressDistrict}
  AND ADDRESS_REST = #{addressRest}
  AND CONTACT_PERSON = #{contactPerson}
  AND CONTACT_NUMBER = #{contactNumber}
  AND ADDRESS_TYPE = #{addressType}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM UME_USER_ADDRESS
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND USER_ID = #{userId}
  AND SEQ = #{seq}
  AND ADDRESS_PROVINCE = #{addressProvince}
  AND ADDRESS_CITY = #{addressCity}
  AND ADDRESS_DISTRICT = #{addressDistrict}
  AND ADDRESS_REST = #{addressRest}
  AND CONTACT_PERSON = #{contactPerson}
  AND CONTACT_NUMBER = #{contactNumber}
  AND ADDRESS_TYPE = #{addressType}
