mcom.umeer.update:
UPDATE EM_TBL_COL_CFG
COL_NAME = #{colName}, DATA_SUB_TYPE = #{dataSubType}, NOT_NULL = #{notNull}, DEFAULT_VALUE = #{defaultValue}, DATA_LENGTH_MIN = #{dataLengthMin}, DATA_LENGTH_MAX = #{dataLengthMax}, DATA_RANGE_MIN = #{dataRangeMin}, DATA_RANGE_MAX = #{dataRangeMax}, CONSTRAINT_TYPE = #{constraintType}, CONSTRAINT_REF = #{constraintRef}, DISP_TYPE = #{dispType}, DISP_FORMAT = #{dispFormat}, EDIT_FORMAT = #{editFormat}, PRE_PROC_DISP = #{preProcDisp}, PRE_PROC_SAVE = #{preProcSave}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ENT_ID = #{entId} AND COL_ID = #{colId} AND UPDATE_DATETIME = #{updateDatetime}

mcom.umeer.select:
SELECT
  ENT_ID AS "entId",
  COL_ID AS "colId",
  COL_NAME AS "colName",
  DATA_SUB_TYPE AS "dataSubType",
  NOT_NULL AS "notNull",
  DEFAULT_VALUE AS "defaultValue",
  DATA_LENGTH_MIN AS "dataLengthMin",
  DATA_LENGTH_MAX AS "dataLengthMax",
  DATA_RANGE_MIN AS "dataRangeMin",
  DATA_RANGE_MAX AS "dataRangeMax",
  CONSTRAINT_TYPE AS "constraintType",
  CONSTRAINT_REF AS "constraintRef",
  DISP_TYPE AS "dispType",
  DISP_FORMAT AS "dispFormat",
  EDIT_FORMAT AS "editFormat",
  PRE_PROC_DISP AS "preProcDisp",
  PRE_PROC_SAVE AS "preProcSave",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_TBL_COL_CFG
WHERE 1 = 1 
  AND ENT_ID = #{entId}
  AND COL_ID = #{colId}
  AND COL_NAME = #{colName}
  AND DATA_SUB_TYPE = #{dataSubType}
  AND NOT_NULL = #{notNull}
  AND DEFAULT_VALUE = #{defaultValue}
  AND DATA_LENGTH_MIN = #{dataLengthMin}
  AND DATA_LENGTH_MAX = #{dataLengthMax}
  AND DATA_RANGE_MIN = #{dataRangeMin}
  AND DATA_RANGE_MAX = #{dataRangeMax}
  AND CONSTRAINT_TYPE = #{constraintType}
  AND CONSTRAINT_REF = #{constraintRef}
  AND DISP_TYPE = #{dispType}
  AND DISP_FORMAT = #{dispFormat}
  AND EDIT_FORMAT = #{editFormat}
  AND PRE_PROC_DISP = #{preProcDisp}
  AND PRE_PROC_SAVE = #{preProcSave}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_TBL_COL_CFG
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ENT_ID = #{entId}
  AND COL_ID = #{colId}
  AND COL_NAME = #{colName}
  AND DATA_SUB_TYPE = #{dataSubType}
  AND NOT_NULL = #{notNull}
  AND DEFAULT_VALUE = #{defaultValue}
  AND DATA_LENGTH_MIN = #{dataLengthMin}
  AND DATA_LENGTH_MAX = #{dataLengthMax}
  AND DATA_RANGE_MIN = #{dataRangeMin}
  AND DATA_RANGE_MAX = #{dataRangeMax}
  AND CONSTRAINT_TYPE = #{constraintType}
  AND CONSTRAINT_REF = #{constraintRef}
  AND DISP_TYPE = #{dispType}
  AND DISP_FORMAT = #{dispFormat}
  AND EDIT_FORMAT = #{editFormat}
  AND PRE_PROC_DISP = #{preProcDisp}
  AND PRE_PROC_SAVE = #{preProcSave}
