mapper.update:
UPDATE EM_CONS_CODE_CFG
CONS_NAME = #{consName}, CONS_DESC = #{consDesc}, DISP_VALUE = #{dispValue}, STORE_VALUE = #{storeValue}, STORE_TYPE = #{storeType}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE CONS_ID = #{consId} AND CONS_SEQ = #{consSeq} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  CONS_ID AS "consId",
  CONS_SEQ AS "consSeq",
  CONS_NAME AS "consName",
  CONS_DESC AS "consDesc",
  DISP_VALUE AS "dispValue",
  STORE_VALUE AS "storeValue",
  STORE_TYPE AS "storeType",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_CONS_CODE_CFG
WHERE 1 = 1 
  AND CONS_ID = #{consId}
  AND CONS_SEQ = #{consSeq}
  AND CONS_NAME = #{consName}
  AND CONS_DESC = #{consDesc}
  AND DISP_VALUE = #{dispValue}
  AND STORE_VALUE = #{storeValue}
  AND STORE_TYPE = #{storeType}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_CONS_CODE_CFG
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND CONS_ID = #{consId}
  AND CONS_SEQ = #{consSeq}
  AND CONS_NAME = #{consName}
  AND CONS_DESC = #{consDesc}
  AND DISP_VALUE = #{dispValue}
  AND STORE_VALUE = #{storeValue}
  AND STORE_TYPE = #{storeType}
