mapper.update:
UPDATE EM_TASK_MGR
TASK_NAME = #{taskName}, TASK_ENABLE = #{taskEnable}, TASK_STATUS = #{taskStatus}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE TASK_ID = #{taskId} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  TASK_ID AS "taskId",
  TASK_NAME AS "taskName",
  TASK_ENABLE AS "taskEnable",
  TASK_STATUS AS "taskStatus",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_TASK_MGR
WHERE 1 = 1 
  AND TASK_ID = #{taskId}
  AND TASK_NAME = #{taskName}
  AND TASK_ENABLE = #{taskEnable}
  AND TASK_STATUS = #{taskStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_TASK_MGR
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND TASK_ID = #{taskId}
  AND TASK_NAME = #{taskName}
  AND TASK_ENABLE = #{taskEnable}
  AND TASK_STATUS = #{taskStatus}
