mapper.update:
UPDATE EM_CONS_SQL_CFG
CONS_NAME = #{consName}, CONS_DESC = #{consDesc}, CONS_SQLR = #{consSqlr}, CONS_SQLU = #{consSqlu}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE CONS_ID = #{consId} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  CONS_ID AS "consId",
  CONS_NAME AS "consName",
  CONS_DESC AS "consDesc",
  CONS_SQLR AS "consSqlr",
  CONS_SQLU AS "consSqlu",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_CONS_SQL_CFG
WHERE 1 = 1 
  AND CONS_ID = #{consId}
  AND CONS_NAME = #{consName}
  AND CONS_DESC = #{consDesc}
  AND CONS_SQLR = #{consSqlr}
  AND CONS_SQLU = #{consSqlu}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_CONS_SQL_CFG
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND CONS_ID = #{consId}
  AND CONS_NAME = #{consName}
  AND CONS_DESC = #{consDesc}
  AND CONS_SQLR = #{consSqlr}
  AND CONS_SQLU = #{consSqlu}
