mapper.update:
UPDATE EM_DS_CFG
NAME = #{name}, URL = #{url}, DRIVER_CLASS = #{driverClass}, USERNAME = #{username}, PASSWORD = #{password}, MAX_WAIT = #{maxWait}, MIN_IDLE = #{minIdle}, MAX_IDLE = #{maxIdle}, MAX_ACTIVE = #{maxActive}, INITIAL_SIZE = #{initialSize}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ID = #{id} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ID AS "id",
  NAME AS "name",
  URL AS "url",
  DRIVER_CLASS AS "driverClass",
  USERNAME AS "username",
  PASSWORD AS "password",
  MAX_WAIT AS "maxWait",
  MIN_IDLE AS "minIdle",
  MAX_IDLE AS "maxIdle",
  MAX_ACTIVE AS "maxActive",
  INITIAL_SIZE AS "initialSize",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_DS_CFG
WHERE 1 = 1 
  AND ID = #{id}
  AND NAME = #{name}
  AND URL = #{url}
  AND DRIVER_CLASS = #{driverClass}
  AND USERNAME = #{username}
  AND PASSWORD = #{password}
  AND MAX_WAIT = #{maxWait}
  AND MIN_IDLE = #{minIdle}
  AND MAX_IDLE = #{maxIdle}
  AND MAX_ACTIVE = #{maxActive}
  AND INITIAL_SIZE = #{initialSize}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_DS_CFG
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ID = #{id}
  AND NAME = #{name}
  AND URL = #{url}
  AND DRIVER_CLASS = #{driverClass}
  AND USERNAME = #{username}
  AND PASSWORD = #{password}
  AND MAX_WAIT = #{maxWait}
  AND MIN_IDLE = #{minIdle}
  AND MAX_IDLE = #{maxIdle}
  AND MAX_ACTIVE = #{maxActive}
  AND INITIAL_SIZE = #{initialSize}
