mapper.update:
UPDATE EM_RESOURCE
RES_NAME = #{resName}, RES_GROUP = #{resGroup}, RES_LINK = #{resLink}, RES_INDEX = #{resIndex}, RES_STATUS = #{resStatus}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE RES_ID = #{resId} AND RES_TYPE = #{resType} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  RES_ID AS "resId",
  RES_TYPE AS "resType",
  RES_NAME AS "resName",
  RES_GROUP AS "resGroup",
  RES_LINK AS "resLink",
  RES_INDEX AS "resIndex",
  RES_STATUS AS "resStatus",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_RESOURCE
WHERE 1 = 1 
  AND RES_ID = #{resId}
  AND RES_TYPE = #{resType}
  AND RES_NAME = #{resName}
  AND RES_GROUP = #{resGroup}
  AND RES_LINK = #{resLink}
  AND RES_INDEX = #{resIndex}
  AND RES_STATUS = #{resStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_RESOURCE
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND RES_ID = #{resId}
  AND RES_TYPE = #{resType}
  AND RES_NAME = #{resName}
  AND RES_GROUP = #{resGroup}
  AND RES_LINK = #{resLink}
  AND RES_INDEX = #{resIndex}
  AND RES_STATUS = #{resStatus}
