mapper.update:
UPDATE EM_USER
USER_NAME = #{userName}, USER_PASSWORD = #{userPassword}, USER_MOBILE = #{userMobile}, USER_MOBILE_BAK = #{userMobileBak}, USER_IDENTITY_CARD = #{userIdentityCard}, USER_DRIVE_LICENSE = #{userDriveLicense}, USER_PASSPORT = #{userPassport}, USER_ADDRESS = #{userAddress}, USER_EMAIL = #{userEmail}, USER_EMAIL_BAK = #{userEmailBak}, USER_LEVEL = #{userLevel}, USER_STATUS = #{userStatus}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE USER_ID = #{userId} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  USER_ID AS "userId",
  USER_NAME AS "userName",
  USER_PASSWORD AS "userPassword",
  USER_MOBILE AS "userMobile",
  USER_MOBILE_BAK AS "userMobileBak",
  USER_IDENTITY_CARD AS "userIdentityCard",
  USER_DRIVE_LICENSE AS "userDriveLicense",
  USER_PASSPORT AS "userPassport",
  USER_ADDRESS AS "userAddress",
  USER_EMAIL AS "userEmail",
  USER_EMAIL_BAK AS "userEmailBak",
  USER_LEVEL AS "userLevel",
  USER_STATUS AS "userStatus",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM EM_USER
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND USER_NAME = #{userName}
  AND USER_PASSWORD = #{userPassword}
  AND USER_MOBILE = #{userMobile}
  AND USER_MOBILE_BAK = #{userMobileBak}
  AND USER_IDENTITY_CARD = #{userIdentityCard}
  AND USER_DRIVE_LICENSE = #{userDriveLicense}
  AND USER_PASSPORT = #{userPassport}
  AND USER_ADDRESS = #{userAddress}
  AND USER_EMAIL = #{userEmail}
  AND USER_EMAIL_BAK = #{userEmailBak}
  AND USER_LEVEL = #{userLevel}
  AND USER_STATUS = #{userStatus}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM EM_USER
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND USER_ID = #{userId}
  AND USER_NAME = #{userName}
  AND USER_PASSWORD = #{userPassword}
  AND USER_MOBILE = #{userMobile}
  AND USER_MOBILE_BAK = #{userMobileBak}
  AND USER_IDENTITY_CARD = #{userIdentityCard}
  AND USER_DRIVE_LICENSE = #{userDriveLicense}
  AND USER_PASSPORT = #{userPassport}
  AND USER_ADDRESS = #{userAddress}
  AND USER_EMAIL = #{userEmail}
  AND USER_EMAIL_BAK = #{userEmailBak}
  AND USER_LEVEL = #{userLevel}
  AND USER_STATUS = #{userStatus}
